import React, { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const onSaveExpenseDataHandler = (enteredExpenseData) => {
    if (enteredExpenseData === -1) {
      setShowForm(<button onClick={formShowcase}>Add New Expense</button>);
    } else {
      const expenseData = {
        ...enteredExpenseData,
        id: Math.random().toString(),
      };

      props.onAddExpense(expenseData);
      setShowForm(<button onClick={formShowcase}>Add New Expense</button>);
    }
  };

  const formShowcase = () => {
    setShowForm(
      <ExpenseForm onSaveExpenseData={onSaveExpenseDataHandler}></ExpenseForm>
    );
  };

  const [showForm, setShowForm] = useState(
    <button onClick={formShowcase}>Add New Expense</button>
  );

  return (
    <div className="new-expense ">
      {showForm}
      {/* <ExpenseForm onSaveExpenseData={onSaveExpenseDataHandler}></ExpenseForm> */}
    </div>
  );
};

export default NewExpense;
