  //         <label>Title</label>
  //         <input
  //           type="text"
  //           value={enteredTitle}
  //           onChange={titleChangeHandler}
  //         ></input>
  //       </div>
  //       <div className="new-expense__control">
  //         <label>Amount</label>
  //         <input
  //           type="number"
  //           value={enteredAmount}
  //           min="0.01"
  //           step="0.01"
  //           onChange={amountChangeHandler}
  //         ></input>
  //       </div>
  //       <div className="new-expense__control">
  //         <label>Date</label>
  //         <input
  //           type="date"
  //           value={enteredDate}
  //           min="2019-01-01"
  //           max="2022-12-31"
  //           onChange={dateChangeHandler}
  //         ></input>
  //       </div>
  //     </div>
  //     <div className="new-expense__actions">
  //       <button type="submit">Add Expense</button>
